server/db.ts.content:
import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

client/src/index.css.content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Sora:wght@300;400;500;600;700;800&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Deep charcoal backgrounds */
  --background: hsl(230, 70%, 3%);
  --foreground: hsl(210, 40%, 98%);

  /* Card surfaces with subtle elevation */
  --card: hsl(230, 60%, 6%);
  --card-foreground: hsl(210, 40%, 98%);

  /* Popover */
  --popover: hsl(230, 60%, 6%);
  --popover-foreground: hsl(210, 40%, 98%);

  /* Rich cobalt primary */
  --primary: hsl(221, 100%, 58%);
  --primary-foreground: hsl(0, 0%, 100%);

  /* Teal secondary */
  --secondary: hsl(180, 80%, 45%);
  --secondary-foreground: hsl(0, 0%, 100%);

  /* Muted backgrounds */
  --muted: hsl(230, 40%, 12%);
  --muted-foreground: hsl(215, 20%, 65%);

  /* Warm ember accent */
  --accent: hsl(14, 100%, 67%);
  --accent-foreground: hsl(0, 0%, 100%);

  /* Lavender highlight */
  --highlight: hsl(255, 65%, 75%);
  --highlight-foreground: hsl(230, 70%, 10%);

  /* Destructive */
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);

  /* Borders and inputs */
  --border: hsl(230, 40%, 15%);
  --input: hsl(230, 40%, 12%);
  --ring: hsl(221, 100%, 58%);

  /* Chart colors */
  --chart-1: hsl(221, 100%, 58%);
  --chart-2: hsl(180, 80%, 45%);
  --chart-3: hsl(255, 65%, 75%);
  --chart-4: hsl(14, 100%, 67%);
  --chart-5: hsl(340, 75%, 55%);

  /* Border radius */
  --radius: 1rem;

  /* Typography */
  --font-heading: 'Sora', 'Inter', system-ui, sans-serif;
  --font-body: 'Inter', system-ui, sans-serif;
  --font-mono: 'JetBrains Mono', 'Menlo', monospace;
}

.dark {
  --background: hsl(230, 70%, 3%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(230, 60%, 6%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(230, 60%, 6%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(221, 100%, 58%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(180, 80%, 45%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(230, 40%, 12%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(14, 100%, 67%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(230, 40%, 15%);
  --input: hsl(230, 40%, 12%);
  --ring: hsl(221, 100%, 58%);
}

@layer base {
  * {
    @apply border-border;
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  /* Lazy Loading Optimization */
  img[loading="lazy"] {
    opacity: 0;
    transition: opacity 0.3s;
  }

  img[loading="lazy"].loaded {
    opacity: 1;
  }

  /* Accessibility Improvements */
  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border-width: 0;
  }

  /* Focus Visible for Keyboard Navigation */
  *:focus-visible {
    outline: 2px solid hsl(var(--primary));
    outline-offset: 2px;
    border-radius: 4px;
  }

  /* Hide Scrollbar */
  .hide-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-family: var(--font-body);
    overflow-x: hidden;
    background: radial-gradient(ellipse at top, hsl(230, 60%, 8%) 0%, hsl(230, 70%, 3%) 50%, hsl(230, 80%, 2%) 100%);
    background-attachment: fixed;
  }

  html {
    scroll-behavior: auto;
  }

  /* Typography Scale */
  h1 {
    font-family: var(--font-heading);
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.1;
    letter-spacing: -0.02em;
  }

  h2 {
    font-family: var(--font-heading);
    font-size: clamp(2rem, 4vw, 2.5rem);
    font-weight: 600;
    line-height: 1.2;
    letter-spacing: -0.01em;
  }

  h3 {
    font-family: var(--font-heading);
    font-size: clamp(1.5rem, 3vw, 1.75rem);
    font-weight: 600;
    line-height: 1.3;
  }

  p {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.7;
    color: hsl(215, 20%, 75%);
  }
}

@layer components {
  /* Glass morphism with enhanced depth */
  .glass-morphism {
    background: linear-gradient(
      135deg,
      rgba(255, 255, 255, 0.08) 0%,
      rgba(255, 255, 255, 0.04) 100%
    );
    backdrop-filter: blur(16px) saturate(180%);
    -webkit-backdrop-filter: blur(16px) saturate(180%);
    border: 1px solid rgba(255, 255, 255, 0.12);
    box-shadow: 
      0 8px 32px rgba(0, 0, 0, 0.3),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  /* Premium glass card */
  .glass-card {
    background: linear-gradient(
      135deg,
      rgba(39, 86, 255, 0.08) 0%,
      rgba(24, 200, 200, 0.05) 100%
    );
    backdrop-filter: blur(20px) saturate(180%);
    -webkit-backdrop-filter: blur(20px) saturate(180%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 
      0 12px 40px rgba(0, 0, 0, 0.4),
      inset 0 1px 0 rgba(255, 255, 255, 0.15);
    transition: all 400ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-card:hover {
    transform: translateY(-4px) scale(1.02);
    border-color: rgba(39, 86, 255, 0.3);
    box-shadow: 
      0 20px 60px rgba(39, 86, 255, 0.2),
      inset 0 1px 0 rgba(255, 255, 255, 0.2);
  }

  /* Gradient text effects */
  .gradient-text {
    background: linear-gradient(
      135deg,
      hsl(221, 100%, 68%) 0%,
      hsl(180, 80%, 55%) 50%,
      hsl(255, 65%, 75%) 100%
    );
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    color: transparent;
  }

  .gradient-text-warm {
    background: linear-gradient(
      135deg,
      hsl(14, 100%, 67%) 0%,
      hsl(340, 75%, 55%) 100%
    );
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    color: transparent;
  }

  /* Modern tech grid */
  .tech-grid {
    background-image: 
      linear-gradient(rgba(39, 86, 255, 0.08) 1px, transparent 1px),
      linear-gradient(90deg, rgba(39, 86, 255, 0.08) 1px, transparent 1px);
    background-size: 64px 64px;
  }

  /* Smooth hover lift */
  .hover-lift {
    transition: all 400ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-lift:hover {
    transform: translateY(-8px);
    box-shadow: 0 24px 48px rgba(39, 86, 255, 0.25);
  }

  /* Logo glow effect */
  .logo-container {
    position: relative;
    display: inline-block;
  }

  .logo-glow {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 140%;
    height: 140%;
    background: radial-gradient(
      circle,
      rgba(39, 86, 255, 0.4) 0%,
      rgba(24, 200, 200, 0.2) 40%,
      transparent 70%
    );
    border-radius: 50%;
    animation: pulse-glow 4s ease-in-out infinite;
    filter: blur(40px);
  }

  /* Parallax background */
  .parallax-bg {
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
  }

  @media (max-width: 768px) {
    .parallax-bg {
      background-attachment: scroll;
    }
  }

  /* Section container */
  .section-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 6rem 2rem;
  }

  @media (max-width: 768px) {
    .section-container {
      padding: 4rem 1.5rem;
    }
  }
}

@layer utilities {
  /* Float animation */
  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse 4s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 3s ease-in-out infinite alternate;
  }

  .animate-slide-up {
    animation: slideUp 0.6s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-slide-right {
    animation: slideRight 0.6s cubic-bezier(0.4, 0, 0.2, 1);
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
  }

  @keyframes pulse-glow {
    0%, 100% { 
      opacity: 0.5;
      transform: translate(-50%, -50%) scale(1);
    }
    50% { 
      opacity: 1;
      transform: translate(-50%, -50%) scale(1.1);
    }
  }

  @keyframes glow {
    0% { 
      box-shadow: 0 0 20px rgba(39, 86, 255, 0.3),
                  0 0 40px rgba(24, 200, 200, 0.2);
    }
    100% { 
      box-shadow: 0 0 40px rgba(39, 86, 255, 0.6),
                  0 0 80px rgba(24, 200, 200, 0.4);
    }
  }

  @keyframes slideUp {
    0% { 
      opacity: 0; 
      transform: translateY(40px);
    }
    100% { 
      opacity: 1; 
      transform: translateY(0);
    }
  }

  @keyframes slideRight {
    0% { 
      opacity: 0; 
      transform: translateX(-40px);
    }
    100% { 
      opacity: 1; 
      transform: translateX(0);
    }
  }

  @keyframes loadingBar {
    0% { width: 0%; }
    50% { width: 70%; }
    100% { width: 100%; }
  }

  @keyframes shimmer {
    0% {
      background-position: -1000px 0;
    }
    100% {
      background-position: 1000px 0;
    }
  }

  .loading-bar {
    animation: loadingBar 2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-shimmer {
    animation: shimmer 3s linear infinite;
    background: linear-gradient(
      90deg,
      rgba(255, 255, 255, 0) 0%,
      rgba(255, 255, 255, 0.1) 50%,
      rgba(255, 255, 255, 0) 100%
    );
    background-size: 1000px 100%;
  }

  /* Hide Scrollbar */
  .hide-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }

  /* Smooth transitions */
  .transition-smooth {
    transition: all 400ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .transition-fast {
    transition: all 200ms cubic-bezier(0.4, 0, 0.2, 1);
  }
}

