            {title}
          </h3>
          <p className="text-muted-foreground leading-relaxed">
            {description}
          </p>
        </div>
      </motion.div>
    );
  };

  const BenefitCard = ({ icon: Icon, title, description, featured = false }: any) => {
    const ref = useRef(null);
