/* === ContactPage.module.css === */
/* Sirf Contact Page ke liye dedicated styling. */

.contactPageWrapper { background-color: #0A0F1F; }

.auroraBackground {
  position: fixed; top: 0; left: 0; width: 100%; height: 100%;
  z-index: 0; opacity: 0.3; overflow: hidden; filter: blur(150px);
}
.auroraShape { position: absolute; border-radius: 50%; mix-blend-mode: plus-lighter; }
.shape1 {
  width: 700px; height: 700px; background: radial-gradient(circle, #00FF99, transparent 60%);
  top: -20%; left: -20%; animation: move-aurora-contact-1 20s infinite alternate ease-in-out;
}
.shape2 {
  width: 600px; height: 600px; background: radial-gradient(circle, #00CFFF, transparent 60%);
  bottom: -30%; right: -25%; animation: move-aurora-contact-2 25s infinite alternate ease-in-out;
}
.shape3 {
  width: 500px; height: 500px; background: radial-gradient(circle, #8A2BE2, transparent 60%);
  bottom: 10%; left: 15%; animation: move-aurora-contact-3 22s infinite alternate ease-in-out;
}
@keyframes move-aurora-contact-1 { to { transform: translate(30vw, 20vh) rotate(80deg) scale(1.3); } }
@keyframes move-aurora-contact-2 { to { transform: translate(-35vw, -10vh) rotate(-90deg) scale(1); } }
@keyframes move-aurora-contact-3 { to { transform: translate(20vw, -25vh) rotate(60deg) scale(1.2); } }

.holographicGrid {
  position: fixed; top: 0; left: 0; width: 100%; height: 100%; z-index: 1;
  background-image: linear-gradient(hsl(var(--primary)/0.1) 1px, transparent 1px), linear-gradient(90deg, hsl(var(--primary)/0.1) 1px, transparent 1px);
  background-size: 70px 70px; perspective: 500px; animation: move-grid-contact 60s linear infinite;
}
@keyframes move-grid-contact {
  0% { background-position: 0 0; transform: rotateX(30deg); }
  100% { background-position: 700px 700px; transform: rotateX(30deg); }
}

.intelligentFormBorder {
  position: relative; background: linear-gradient(145deg, hsl(var(--border)/0.5), hsl(var(--border)/0.2));
}
.intelligentFormBorder::before {
  content: ""; position: absolute; inset: 0; border-radius: 24px; padding: 1px;
  background: radial-gradient(400px circle at var(--mouse-x, 0) var(--mouse-y, 0), hsl(var(--primary)/0.8), transparent 50%);
  -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  -webkit-mask-composite: xor; mask-composite: exclude; pointer-events: none;
}
.formInnerBg { background-color: hsl(var(--background)/0.6); backdrop-filter: blur(20px); }

